    val listData: ArrayList<Mainan>
        get() {
            val list = arrayListOf<Mainan>()
            for (position in mainanTitles.indices) {
                val mainan = Mainan()
                mainan.id = id[position]
                mainan.title = mainanTitles[position]
                mainan.subtitle = mainanSubtitles[position]
                mainan.photo = mainanPhotos[position]
                list.add(mainan)
            }
            return list
        }
